import {makeAutoObservable} from "mobx"

export default class UserStore {
    constructor() {
        this._users = [
            {id: 1, nickname: 'Superman', real_name: 'Clark Kent', img: `46c89bcb-f23e-44fa-be43-b2f8cca0a095.jpg`},
            {id: 2, name: 'DC'}
        ]

        makeAutoObservable(this)
    }

    setUsers(users) {
        this._users = users
    }

    get user() {
        return this._users
    }
}
