    }

    return (
        <Modal 
        show={show} 
        onHide={onHide}>
        <Modal.Header closeButton>
          <Modal.Title>ADD Info Hero</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
            <Form.Control 
            className="mt-3"
            value={nickname}
                placeholder={"Lead the hero's nickname"}
                />
                 <Form.Control 
                 value={realName}
                 className="mt-3"
                placeholder={"Lead the hero's real name"}
                />
                 <Form.Control 
                 className="mt-3"
                 value={superpowers}
                placeholder={"Lead the hero's superpowers"}
                />
                 <Form.Control 
                 className="mt-3"
                 value={originDescription}
                placeholder={"Lead the hero's origin description"}
                />
                 <Form.Control 
                 className="mt-3"
                 value={catchPhase}
                placeholder={"Lead the hero's catch phase"}
                />
                <Form.Control 
                className="mt-3"
                value={image}
                placeholder={"Loading an image"}
                type="file"
                onChange={selectFile}
                />
            </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="outline-danger" onClick={onHide}>
            Close
          </Button>
          <Button variant="outline-success" onClick={addUser}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    )
}

export default CreateUser
