import React from "react";
import { Col, Container, Image, Row, Card, Button } from "react-bootstrap";

const UserHero = () => {
  const user = [
    {id: 6,
    nickname: "Superman",
    real_name: "Clark Kent",
    img:
      "https://i.guim.co.uk/img/media/fb18c7fae89eba7df3adc05dd73868c9919e3abb/0_131_4000_2400/master/4000.jpg?width=1200&height=900&quality=85&auto=format&fit=crop&s=ec9617bbb167fce941f33c09ae4937a3",
  }];
  const description = [
    {
      id: 1,
      origin_description: `He was born Kal-El on the planet Krypton, before being rocketed to
        Earth as an infant by his scientist father Jor-El, moments before Krypton's destruction…`,
      superpowers: `Solar energy absorption and healing factor, solar flare and heat vision,
        solar invulnerability, flight…`,
      catch_phrase: `“Look, up in the sky, it's a bird, it's a plane, it's Superman!”`,
    },
  ];

  return (
    <Container className="mt-3">
      <Row>
        <Col md={4}>
          <Image width={300} height={300} src={user.img} />
        </Col>
        <Col md={4}>
          <Row className="d-flax flex-column align-items-center">
            <div className="d-flex align-items-center justify-content-center">
              <Image width={300} height={300} src={user.img} />
            </div>
          </Row>
        </Col>
        <Col md={4}>
          <Card
            className="d-flex flex-column align-items-center justify-content-around"
            style={{
              width: 300,
              height: 300,
              fontSize: 32,
              border: "5px solid lightgray",
            }}
          >
            <h3>From: $.</h3>
            <Button variant={"outline-dark"}>Add to Cart</Button>
          </Card>
        </Col>
      </Row>
      <Row className="d-flex flex-column m-3">
        <h1>Info</h1>
        {description.map((info, index) => (
          <Row
            key={info.id}
            style={{
              background: index % 2 === 0 ? "lightgray" : "transparent",
              padding: 10,
            }}
          >
            <Col>
              Description:
              <br />
              {info.origin_description}:
            </Col>
            <Col>
              Superpowers:
              <br />
              {info.superpowers}
            </Col>
            <Col>
              Catch phrase:
              <br />
              {info.catch_phrase}
            </Col>
          </Row>
        ))}
      </Row>
    </Container>
  );
};

export default UserHero;
