            {id: 5, nickname: 'Superman', real_name: 'Clark Kent', img: 'https://static.wikia.nocookie.net/marvel_dc/images/a/a5/Superman_Vol_5_1_Textless.jpg/revision/latest?cb=20180711061148'},
    
        ]

        makeAutoObservable(this)
    }

    setUsers(users) {
        this._users = users
    }

    get users() {
        return this._users
    }
}
